import React, { useEffect, useState } from "react";
import Transaction from "./Transaction";

function TransactionsList({ onUpdateTransactions }) {
  const [transactionsData, setTransactionsData] = useState([]);

  useEffect(() => {
    // Fetch data from the provided JSON server
    fetch("http://localhost:8001/transactions")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setTransactionsData(data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  // Function to update transactions list after adding a new transaction
  const updateTransactionsList = (newTransaction) => {
    setTransactionsData([...transactionsData, newTransaction]);
    onUpdateTransactions([...transactionsData, newTransaction]); // Update parent state
  };

